      </svg>
      <div
        ref="chequeContent"
        class="contents"
      >
        <div class="restaurant">
          {{ cheque.restaurant }}
          <div class="rev-center">
            {{ cheque.revenueCenter }}
          </div>
        </div>
        <div class="divider" />
        <div class="summary">
          <div class="summary-column">
            <div class="open-time">
              <div class="summary-item-label">
                Opened at
              </div>
              <div>{{ openTime }}</div>
            </div>
            <div class="close-time">
              <div class="summary-item-label">
                Closed at
              </div>
              <div>{{ closeTime }}</div>
            </div>
          </div>
          <div class="summary-column right-align">
            <div class="cheque-number">
              <div class="summary-item-label">
                Check no.
              </div>
              <div>{{ cheque.number }}</div>
            </div>
            <div class="server">
              <div class="summary-item-label">
                Server
              </div>
              <div>{{ cheque.server }}</div>
            </div>
          </div>
        </div>
        <div
          v-if="cheque.items.length > 0"
          class="divider"
        />
        <div class="items">
          <div
            v-if="cheque.items.length > 0"
            class="section-title"
          >
            Items ordered
          </div>
          <div
            v-for="(item, idx) in cheque.items"
            :key="idx"
            class="item"
          >
            <div class="order-time">
              {{ formatTime(item.orderTime) }}
            </div>
            <div class="item-name">
              {{ item.item.name }}
            </div>
            <div class="item-spacer" />
            <div class="item-quantity">
              {{ `x${item.quantity}` }}
            </div>
            <div class="item-amount">
              {{ formatAmount(item.amount) }}
            </div>
          </div>
        </div>
        <div class="items">
          <div
            v-if="cheque.items.filter(item => item.isVoid).length > 0"
            class="section-title"
          >
            Voided items
          </div>
          <div
            v-for="(item, idx) in cheque.items.filter(item => item.isVoid)"
            :key="idx"
            class="item"
          >
            <div class="order-time">
              {{ formatTime(item.orderTime) }}
            </div>
            <div class="item-name">
              {{ item.item.name }}
            </div>
            <div class="item-spacer" />
            <div class="item-quantity">
              {{ `x${item.quantity}` }}
            </div>
            <div class="item-amount">
              {{ formatAmount(-item.amount) }}
            </div>
          </div>
        </div>
        <div class="totals">
          <div class="section-title">
            Totals
          </div>
          <div class="subtotals-row">
            subtotal
            <div class="item-spacer" />
            <div class="item-amount">
              {{ formatAmount(cheque.subTotal) }}
            </div>
          </div>
          <div class="subtotals-row">
            discounts
            <div class="item-spacer" />
            <div class="item-amount">
              {{ formatAmount(-cheque.discounts) }}
            </div>
          </div>
          <div class="subtotals-row">
            tax
            <div class="item-spacer" />
            <div class="item-amount">
              {{ formatAmount(cheque.tax) }}
            </div>
          </div>
          <div class="subtotals-row">
            tip
            <div class="item-spacer" />
            <div class="item-amount">
              {{ formatAmount(cheque.tip) }}
            </div>
          </div>
          <div class="subtotals-row">
            total
            <div class="item-spacer" />
            <div class="item-amount">
              {{ formatAmount(cheque.paid) }}
            </div>
          </div>
        </div>
        <div class="divider" />
        <div class="payments">
          <div class="section-title">
            Payments made
          </div>
          <div
            v-for="(payment, idx) in cheque.payments"
            :key="idx"
            class="payment"
          >
            <div class="payment-method-name">
              {{ payment.method.name }}
            </div>
            <div class="payment-spacer" />
            <div class="payment-amount">
              {{ formatAmount(payment.amount) }}
            </div>
          </div>
        </div>
      </div>
